import React from 'react';
import { Card, Image, Jumbotron } from 'react-bootstrap'
import { isUserLoggedIn } from '../../actions';
import whiteimg from "./images/White.png";
import Layout from '../../components/Layout'
import { Container, Form, Row, Col, Button } from 'react-bootstrap';
import './style.css';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';

/**
* @author
* @function HomePage
**/

const HomePage = (props) => {
    const auth = useSelector(state => state.auth);

    return (
        <Layout sidebar>

            <Jumbotron margin='5rem' className="text-center">
                <h1>Welcome to the Admin Dashboard <b><a className="fullName">{auth.user.fullName}</a></b></h1>
                <Col>
                    <Row>
                        <Col>

                            <Card className="bg-info text-white"

                                style={{ width: '18rem' }}

                            >

                                <Card.Body>
                                    <Container>
                                        <Image src={whiteimg} roundedCircle className="pl-5 pr-5 pt-1 pb-3" />
                                    </Container>



                                    <Card.Title style={{ textAlign: "center" }}> <h3> Card Title </h3> </Card.Title>
                                    <Card.Text>
                                        Some quick example text to build on the card title and make up the bulk
                                        of the card's content.
      </Card.Text>
                                </Card.Body>
                            </Card>

                        </Col>
                    </Row>
                </Col>
            </Jumbotron>
            <Row><br></br></Row>


        </Layout>
    )

}

export default HomePage
